INSERT INTO `pestlib` VALUES (10001,'稻飞虱','飞虱科','稻飞虱，昆虫纲同翅目(Homoptera)飞虱科(Delphacidae)害虫。俗名蠓子虫。以刺吸植株汁液危害水稻等作物。常见种类有褐飞虱（Nilaparvata lugens）、白背飞虱(Sogatella furcifera)和灰飞虱(Laodelphax striatellus)。','使用敌敌畏','../HN_upload\\pestlib/稻飞虱.jpeg'),(10002,'水稻螟虫','螟蛾科','水稻螟虫俗称钻心虫，其中普遍发生较严重的主要是二化螟和三化螟，还有稻苞虫、大螟等。二化螟除为害水稻外还为害玉米、小麦等禾本科作物，三化螟为单食性害虫，只为害水稻。','注射农药','../HN_upload\\pestlib/水稻螟虫.jpg'),(10003,'白粉虱',NULL,NULL,NULL,'../HN_upload\\pestlib/白粉虱.jpeg'),(10004,'介壳虫',NULL,NULL,NULL,'../HN_upload\\pestlib/介壳虫.jpg'),(10005,'蚜虫',NULL,NULL,NULL,'../HN_upload\\pestlib/蚜虫.jpg'),(10006,'棉红蜘蛛',NULL,NULL,NULL,'../HN_upload\\pestlib/棉红蜘蛛.jpg'),(10007,'卷叶蛾',NULL,NULL,NULL,'../HN_upload\\pestlib/卷叶蛾.jpg'),(10008,'地老虎',NULL,NULL,NULL,'../HN_upload\\pestlib/地老虎.jpeg'),(10009,'蓟马',NULL,NULL,NULL,'../HN_upload\\pestlib/蓟马.jpg'),(10010,'水稻赤斑黑沫蝉',NULL,NULL,NULL,'../HN_upload\\pestlib/水稻赤斑黑沫蝉.jpeg'),(10011,'天牛',NULL,NULL,NULL,'../HN_upload\\pestlib/天牛.jpeg'),(10012,'烟青虫',NULL,NULL,NULL,'../HN_upload\\pestlib/烟青虫.jpg'),(10013,'吊丝虫',NULL,NULL,NULL,'../HN_upload\\pestlib/吊丝虫.jpeg');
